import React from 'react';
import { 
  Cog6ToothIcon, 
  SparklesIcon, 
  ShieldCheckIcon, 
  HeartIcon,
  EyeIcon,
  ClockIcon
} from '@heroicons/react/24/outline';

const Services = () => {
  const services = [
    {
      icon: Cog6ToothIcon,
      title: 'General Dentistry',
      description: 'Comprehensive dental care including cleanings, fillings, and preventive treatments to maintain your oral health.',
      color: 'bg-primary'
    },
    {
      icon: SparklesIcon,
      title: 'Cosmetic Dentistry',
      description: 'Transform your smile with professional whitening, veneers, and other cosmetic procedures.',
      color: 'bg-accent'
    },
    {
      icon: ShieldCheckIcon,
      title: 'Emergency Care',
      description: '24/7 emergency dental services for urgent dental problems and pain relief.',
      color: 'bg-primary'
    }
  ];

  return (
    <section id="services" className="section-padding bg-light-gray">
      <div className="container-custom">
        <div className="text-center mb-16">
          <h2 className="font-heading font-bold text-4xl md:text-5xl mb-6 text-text-dark tracking-wide">
            Our Services
          </h2>
          <p className="text-xl text-text-dark/70 max-w-3xl mx-auto leading-relaxed">
            We offer a comprehensive range of dental services to meet all your oral health needs. 
            From routine checkups to advanced cosmetic procedures, we're here to help you achieve a healthy, beautiful smile.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <div
              key={index}
              className="bg-white rounded-2xl p-8 shadow-lg card-hover border border-border-gray"
            >
              <div className={`w-16 h-16 ${service.color} rounded-full flex items-center justify-center mb-6 shadow-lg`}>
                <service.icon className="h-8 w-8 text-white" />
              </div>

              <h3 className="font-heading font-semibold text-2xl mb-4 text-text-dark">
                {service.title}
              </h3>

              <p className="text-text-dark/70 leading-relaxed mb-6">
                {service.description}
              </p>

              <button className="text-primary font-semibold hover:text-accent transition-colors duration-300 flex items-center gap-2">
                Learn More
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                </svg>
              </button>
            </div>
          ))}
        </div>

        <div className="text-center mt-16">
          <button className="btn-primary text-lg px-10 py-5">
            View All Services
          </button>
        </div>
      </div>
    </section>
  );
};

export default Services;
