import React from 'react';
import { 
  Cog6ToothIcon, 
  SparklesIcon, 
  ShieldCheckIcon
} from '@heroicons/react/24/outline';
import { motion } from 'framer-motion';
import { useScrollAnimation, staggerContainer, fadeInUp, scaleIn } from '../hooks/useScrollAnimation';

const Services = () => {
  const services = [
    {
      icon: Cog6ToothIcon,
      title: 'General Dentistry',
      description: 'Comprehensive dental care including cleanings, fillings, and preventive treatments to maintain your oral health.',
      color: 'bg-primary'
    },
    {
      icon: SparklesIcon,
      title: 'Cosmetic Dentistry',
      description: 'Transform your smile with professional whitening, veneers, and other cosmetic procedures.',
      color: 'bg-primary'
    },
    {
      icon: ShieldCheckIcon,
      title: 'Emergency Care',
      description: '24/7 emergency dental services for urgent dental problems and pain relief.',
      color: 'bg-primary'
    }
  ];

  return (
    <section id="services" className="section-padding bg-light-blue">
      <div className="container-custom">
        <div className="text-center mb-16">
          <div className="text-primary text-sm font-medium mb-4">+ OUR SERVICES</div>
          <h2 className="font-heading font-bold text-4xl md:text-5xl mb-6 text-text-dark tracking-wide">
            High Quality Services for You
          </h2>
          <p className="text-xl text-text-light max-w-3xl mx-auto leading-relaxed">
            We are committed to sustainability. eco-friendly initiatives.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
          {services.map((service, index) => (
            <div
              key={index}
              className="bg-white rounded-2xl p-8 shadow-lg card-hover border border-border-gray"
            >
              <div className={`w-16 h-16 ${service.color} rounded-full flex items-center justify-center mb-6 shadow-lg`}>
                <service.icon className="h-8 w-8 text-white" />
              </div>

              <h3 className="font-heading font-semibold text-xl mb-4 text-text-dark">
                {service.title}
              </h3>

              <p className="text-text-light leading-relaxed mb-6">
                We are excited to meet you and provide the best dental care for your family.
              </p>

              <button className="text-primary font-semibold hover:text-primary/80 transition-colors duration-300 flex items-center gap-2">
                Read More
                <div className="w-6 h-6 bg-primary rounded-full flex items-center justify-center">
                  <svg className="w-3 h-3 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                  </svg>
                </div>
              </button>
            </div>
          ))}
        </div>

        <div className="text-center mt-16">
          <p className="text-text-light mb-6">
            We believe in using the latest technology and techniques to ensure the best outcomes for our patients.
          </p>
          <button className="btn-primary text-lg px-10 py-5">
            View All Services
          </button>
        </div>
      </div>
    </section>
  );
};

export default Services;
