import React from 'react';
import { 
  Cog6ToothIcon, 
  SparklesIcon, 
  ShieldCheckIcon, 
  HeartIcon,
  EyeIcon,
  ClockIcon
} from '@heroicons/react/24/outline';

const Services = () => {
  const services = [
    {
      icon: ToothIcon,
      title: 'General Dentistry',
      description: 'Comprehensive dental care including cleanings, fillings, and preventive treatments to maintain your oral health.',
      color: 'bg-primary-500'
    },
    {
      icon: SparklesIcon,
      title: 'Cosmetic Dentistry',
      description: 'Transform your smile with professional whitening, veneers, and other cosmetic procedures.',
      color: 'bg-secondary'
    },
    {
      icon: ShieldCheckIcon,
      title: 'Emergency Care',
      description: '24/7 emergency dental services for urgent dental problems and pain relief.',
      color: 'bg-accent'
    },
    {
      icon: HeartIcon,
      title: 'Family Dentistry',
      description: 'Gentle dental care for the whole family, from children to seniors, in a comfortable environment.',
      color: 'bg-primary-600'
    },
    {
      icon: EyeIcon,
      title: 'Advanced Diagnostics',
      description: 'State-of-the-art imaging and diagnostic tools for accurate treatment planning.',
      color: 'bg-secondary'
    },
    {
      icon: ClockIcon,
      title: 'Same Day Appointments',
      description: 'Quick access to dental care with flexible scheduling and same-day emergency appointments.',
      color: 'bg-accent'
    }
  ];

  return (
    <section id="services" className="section-padding bg-neutral">
      <div className="container-custom">
        <div className="text-center mb-16">
          <h2 className="font-heading font-bold text-4xl md:text-5xl mb-4 text-text">
            Our Services
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            We offer a comprehensive range of dental services to meet all your oral health needs. 
            From routine checkups to advanced cosmetic procedures, we're here to help you achieve a healthy, beautiful smile.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <div
              key={index}
              className="bg-white rounded-xl p-8 shadow-lg card-hover border border-gray-100"
            >
              <div className={`w-16 h-16 ${service.color} rounded-lg flex items-center justify-center mb-6`}>
                <service.icon className="h-8 w-8 text-white" />
              </div>

              <h3 className="font-heading font-semibold text-xl mb-4 text-text">
                {service.title}
              </h3>

              <p className="text-gray-600 leading-relaxed">
                {service.description}
              </p>

              <button className="mt-6 text-primary-500 font-semibold hover:text-primary-600 transition-colors duration-300 flex items-center gap-2">
                Learn More
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                </svg>
              </button>
            </div>
          ))}
        </div>

        <div className="text-center mt-12">
          <button className="btn-primary text-lg px-8 py-4">
            View All Services
          </button>
        </div>
      </div>
    </section>
  );
};

export default Services;
