import React from 'react';
import { 
  ShieldCheckIcon, 
  ClockIcon, 
  HeartIcon 
} from '@heroicons/react/24/outline';
import { motion } from 'framer-motion';
import { useScrollAnimation, staggerContainer, fadeInUp, scaleIn } from '../hooks/useScrollAnimation';

const WhyChooseUs = () => {
  const reasons = [
    {
      icon: ShieldCheckIcon,
      title: 'Trusted & Experienced',
      description: 'Our team of dental professionals has over 15 years of combined experience, ensuring you receive the highest quality care.',
      color: 'bg-primary'
    },
    {
      icon: ClockIcon,
      title: 'Convenient & Flexible',
      description: 'We offer flexible scheduling, same-day appointments, and extended hours to fit your busy lifestyle.',
      color: 'bg-primary'
    },
    {
      icon: HeartIcon,
      title: 'Patient-Centered Care',
      description: 'We prioritize your comfort and well-being, providing personalized treatment plans and a warm, welcoming environment.',
      color: 'bg-primary'
    }
  ];

  const { ref, isInView } = useScrollAnimation();

  return (
    <section className="section-padding bg-light-gray">
      <div className="container-custom">
        <motion.div 
          className="text-center mb-16"
          ref={ref}
          initial="hidden"
          animate={isInView ? "visible" : "hidden"}
          variants={fadeInUp}
        >
          <h2 className="font-heading font-bold text-4xl md:text-5xl mb-6 text-text-dark tracking-wide">
            Why Choose Us
          </h2>
          <p className="text-xl text-text-dark/70 max-w-3xl mx-auto leading-relaxed">
            We're committed to providing exceptional dental care with a focus on comfort, 
            technology, and personalized treatment plans for every patient.
          </p>
        </motion.div>

        <motion.div 
          className="grid grid-cols-1 md:grid-cols-3 gap-8"
          variants={staggerContainer}
          initial="hidden"
          animate={isInView ? "visible" : "hidden"}
        >
          {reasons.map((reason, index) => (
            <motion.div
              key={index}
              className="text-center bg-white rounded-2xl p-8 shadow-lg card-hover border border-border-gray"
              variants={scaleIn}
              whileHover={{ 
                y: -10,
                transition: { duration: 0.3 }
              }}
            >
              <motion.div 
                className={`w-20 h-20 ${reason.color} rounded-full flex items-center justify-center mx-auto mb-6 shadow-lg`}
                whileHover={{ scale: 1.1 }}
                transition={{ duration: 0.2 }}
              >
                <reason.icon className="h-10 w-10 text-white" />
              </motion.div>

              <h3 className="font-heading font-semibold text-2xl mb-4 text-text-dark">
                {reason.title}
              </h3>

              <p className="text-text-dark/70 leading-relaxed">
                {reason.description}
              </p>
            </motion.div>
          ))}
        </motion.div>

        {/* Additional Features */}
        <motion.div 
          className="mt-16 grid grid-cols-2 md:grid-cols-4 gap-6"
          variants={staggerContainer}
          initial="hidden"
          animate={isInView ? "visible" : "hidden"}
        >
          {[
            { number: "24/7", label: "Emergency Care" },
            { number: "100%", label: "Sterilized Equipment" },
            { number: "Latest", label: "Technology" },
            { number: "0%", label: "Pain Guarantee" }
          ].map((item, index) => (
            <motion.div 
              key={index}
              className="text-center"
              variants={fadeInUp}
              whileHover={{ scale: 1.05 }}
            >
              <motion.div 
                className="text-3xl font-bold text-primary mb-2"
                initial={{ scale: 0 }}
                animate={isInView ? { scale: 1 } : { scale: 0 }}
                transition={{ delay: index * 0.1 }}
              >
                {item.number}
              </motion.div>
              <div className="text-sm text-text-dark/70">{item.label}</div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default WhyChooseUs;
