import React from 'react';
import { CheckCircleIcon, ArrowRightIcon } from '@heroicons/react/24/outline';
import { motion } from 'framer-motion';
import { useScrollAnimation, slideInLeft, slideInRight, fadeInUp } from '../hooks/useScrollAnimation';

const About = () => {
  const { ref, isInView } = useScrollAnimation();

  const features = [
    'State-of-the-art dental technology',
    'Experienced and compassionate team',
    'Comfortable and relaxing environment',
    'Comprehensive treatment plans',
    'Emergency care available 24/7',
    'Family-friendly atmosphere'
  ];

  return (
    <section id="about" className="section-padding bg-white">
      <div className="container-custom">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-16 items-center">
          {/* Image Column */}
          <motion.div 
            className="relative"
            ref={ref}
            variants={slideInLeft}
            initial="hidden"
            animate={isInView ? "visible" : "hidden"}
          >
            <div className="relative z-10">
              <div className="w-full h-96 bg-gradient-to-br from-primary to-primary/80 rounded-3xl shadow-2xl">
                <div className="w-full h-full bg-light-blue rounded-3xl flex items-center justify-center">
                  <div className="text-center text-text-dark/60">
                    <div className="w-24 h-24 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-4">
                      <svg className="w-12 h-12 text-primary" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
                      </svg>
                    </div>
                    <p className="text-lg font-medium">Modern Dental Clinic</p>
                    <p className="text-sm">Professional & Caring Environment</p>
                  </div>
                </div>
              </div>
            </div>

            {/* Decorative Elements */}
            <motion.div 
              className="absolute -top-4 -left-4 w-20 h-20 bg-primary/30 rounded-full opacity-20"
              animate={{ 
                scale: [1, 1.2, 1],
                opacity: [0.2, 0.4, 0.2]
              }}
              transition={{ 
                duration: 3, 
                repeat: Infinity,
                ease: "easeInOut"
              }}
            />
            <motion.div 
              className="absolute -bottom-4 -right-4 w-16 h-16 bg-primary/30 rounded-full opacity-20"
              animate={{ 
                scale: [1, 1.3, 1],
                opacity: [0.2, 0.5, 0.2]
              }}
              transition={{ 
                duration: 4, 
                repeat: Infinity,
                ease: "easeInOut",
                delay: 1
              }}
            />
          </motion.div>

          {/* Content Column */}
          <div>
            <div className="text-primary text-sm font-medium mb-4">+ ABOUT US</div>
            <h2 className="font-heading font-bold text-4xl md:text-5xl mb-8 text-text-dark tracking-wide">
              Your Journey to a Healthier Smile Begins Here
            </h2>

            <p className="text-lg text-text-light mb-8 leading-relaxed">
              The goal of our clinic is to provide friendly, caring dentistry and the highest level of general, cosmetic, and specialist dental treatments. With dental practices throughout the world.
            </p>

            {/* Features List */}
            <div className="space-y-4 mb-8">
              {features.map((feature, index) => (
                <div key={index} className="flex items-center gap-3">
                  <CheckCircleIcon className="h-5 w-5 text-primary flex-shrink-0" />
                  <span className="text-text-dark">{feature}</span>
                </div>
              ))}
            </div>

            <button className="btn-primary text-lg px-10 py-5 flex items-center gap-3">
              Read More About Us
              <ArrowRightIcon className="h-5 w-5" />
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
